[mysqld]
port = 63099 #端口
max_connections = 1024 #最大连接数量 default-storage-engine=INNODB
character-set-server=utf8mb4
collation-server = utf8mb4_unicode_ci slow_query_log = on #开启慢查询日志
long_query_time=3 #3秒以上
#slow-query-log-file = /home/db/madb/log/slow-query.log default-authentication-plugin=mysql_native_password table_open_cache=2000 #表文件句柄缓存（表数据是存储在磁盘上的，缓存磁盘文件的句柄方便打开文件读取数据）
key_buffer_size=8388608 #索引缓存大小（将从磁盘上读取的索引缓存到内存，可以设置大一些，有利于快速检索）
innodb_buffer_pool_size=8388608 #Innodb存储引擎缓存池大小（对于Innodb来说最重要的一个配置，如果所有的表用的都是Innodb，那么甚至建议将该值设置到物理内存的80%，Innodb的很多性能提升如索引都是依靠这个） # 客户端来源数据的默认字符集
[client]
default-character-set = utf8mb4 # 数据库默认字符集
[mysql]
default-character-set = utf8mb4 #主从
